$(function(){$(document).on("click","a.dropdown-toggle",function(){$(document).find("td.day").find("div.btn-group").removeClass("forceVisible");$(this).closest("div.btn-group").addClass("forceVisible")});$(document).on("click","a.changeDayState",function(c){c.preventDefault();var d=$(this).closest("td.day");if(d.hasClass("working")){d.find("input").val("H");d.removeClass("working notset").addClass("holyday");$(this).text("Changer en Workingday")}else{d.find("input").val("W");d.removeClass("holyday notset").addClass("working");$(this).text("Changer en Holyday")}d.find("div.btn-group").removeClass("forceVisible");if($(document).find("div.blockbutton").length>0){$(document).find("div.blockbutton").css("display","block")}});$("form#newCalendar").submit(function(){$(this).find("button[type='submit']").replaceWith('<i class="fa fa-spin fa-spinner" style="color:white; font-size: 2.5em;"></i>')});$("form#updateForm").submit(function(){$(this).find("button[type=submit]").replaceWith('<i class="fa fa-spin fa-spinner" style="color:lightgrey; font-size: 2.5em;"></i>')})});